args.fn:
({ ws, payload }) => {
    if (!ws) return { success: false, error: "Workspace not available" };
    // dx, dy 是屏幕像素偏移量（正值表示内容应该向右/向下移动）
    const dx = (payload && payload.dx) || 0;
    const dy = (payload && payload.dy) || 0;
    const scale = ws.scale || 1;

    const canvas = ws.getCanvas();
    if (!canvas) return { success: false, error: "Cannot get canvas" };

    // 获取当前的 transform
    const currentTransform = canvas.getAttribute('transform') || '';

    // 解析当前的 translate 值
    let currentTx = 0, currentTy = 0;
    const translateMatch = currentTransform.match(/translate\(([^,]+),\s*([^)]+)\)/);
    if (translateMatch) {
        currentTx = parseFloat(translateMatch[1]) || 0;
        currentTy = parseFloat(translateMatch[2]) || 0;
    }

    // 在当前基础上增加 dx, dy（屏幕像素偏移）
    const newTx = currentTx + dx;
    const newTy = currentTy + dy;

    // 设置新的 transform（保持原来的 scale）
    const newTransform = 'translate(' + newTx + ',' + newTy + ') scale(' + scale + ')';
    canvas.setAttribute('transform', newTransform);

    // 同时更新 bubble canvas（气泡层）
    const bubbleCanvas = ws.getBubbleCanvas();
    if (bubbleCanvas) {
        bubbleCanvas.setAttribute('transform', newTransform);
    }

    // 更新 scrollX/scrollY 以保持内部状态一致
    // scrollX = -translateX / scale
    ws.scrollX = -newTx / scale;
    ws.scrollY = -newTy / scale;

    return { 
        success: true, 
        mode: "direct_svg_transform",
        inputDx: dx, inputDy: dy,
        currentTransform,
        currentTx, currentTy,
        newTx, newTy,
        newTransform,
        newScrollX: ws.scrollX, 
        newScrollY: ws.scrollY,
        scale
    };
}
